fval.value:
import com.vordel.common.base64.Encoder;

def invoke(msg)         
{        
    def data = msg.get("api.promotion");
    def base64EncodedData = Encoder.encode(data.toString());
    msg.put("data.uri", "data:application/octet-stream;base64," + base64EncodedData);
    return true;        
}
